import React from 'react';

const states = ['Rio de Janeiro', 'Minas Gerais', 'Amapá', 'Amazonas', 'São Paulo', 'Ceará', 'Distrito Federal'];

const INITIAL_STATE = {
    name: '',
    email: '',
    cpf: '',
    address: '',
    city: '',
    countryState: '',
    addressType: '',
};

class Form extends React.Component {
    constructor(props) {
        super(props);
        this.state = INITIAL_STATE;
    }

    validateAddress = address => address.replace(/[^\w\s]/gi, '');

    changeHandle = (event) => {
        const { name } = event.target;
        let { value } = event.target;
        if (name === 'name') value = value.toUpperCase();
        if (name === 'address') value = this.validateAddress(value);
        this.setState({
            [name]: value,
        });
    }

    OnBlurHandle = (event) => {
        const { name } = event.target;
        let { value } = event.target;
        if (name === 'city') value = value.match(/^\d/) ? '' : value;
        this.setState({
            [name]: value,
        })
    }

    render() {
        const { name, address } = this.state;
        return (
            <form>
                <fieldset>
                    <legend>Dados pessoais </legend>
                    <div className='container'>
                        Nome:
                        <input
                            type="name"
                            name="name"
                            maxLength="40"
                            value={name}
                            required
                            onChange={this.changeHandle}
                        />
                    </div>
                    <div className='container'>
                        Email:
                        <input
                            type="email"
                            name="email"
                            maxLength="50"
                            required
                            onChange={this.changeHandle}
                        />
                    </div>
                    <div className='container'>
                        CPF:
                        <input
                            type="text"
                            name="cpf"
                            maxLength="11"
                            required
                            onChange={this.changeHandle}
                        />
                    </div>
                    <div className='container'>
                        Endereço:
                        <input
                            type="text"
                            name="address"
                            maxLength="200"
                            required
                            value={address}
                            onChange={this.changeHandle}
                        />
                    </div>
                    <div className='container'>
                        Cidade:
                        <input
                            type="text"
                            name="city"
                            maxLength="28"
                            required
                            onBlur={this.OnBlurHandle}
                            onChange={this.changeHandle}
                        />
                    </div>
                    <div className='container'>
                        Estado:
                        <select
                            name="countryState"
                            required
                            onChange={this.changeHandle}
                            defaultValue=""
                        >
                            <option value="">Selecione</option>
                            {
                                states.map((value, key) => (
                                    <option key={key}>{value}</option>
                                ))
                            }
                        </select>
                    </div>
                    <div className="container">
                        <label htmlFor="house">
                            <input
                                type="radio"
                                id="house"
                                name="adressType"
                                value="house"
                                onChange={this.changeHandle}
                            />
                            Casa
                        </label>
                        <label htmlFor="apart">
                            <input
                                type="radio"
                                id="apart"
                                name="adressType"
                                value="apartament"
                                onChange={this.changeHandle}
                            />
                            Apartamento
                        </label>
                    </div>
                </fieldset>
            </form>
        )
    };
}

export default Form;
